function [p,q] = davis(N)    em_const = 0.577215664901533;    bests = [1];    best_p = 1;    best_q = 1;        if N == 1        best_p = 0;        best_q = 1;    else        for p = 1:N            for q = 1:N                if abs(p/q-em_const)<abs(bests(end)-em_const)                   bests = [bests, p/q];                   best_p = p;                   best_q = q;                end                if p+q>=N                    break                end            end        end    end    p = best_p;    q = best_q;end