function result = isLucky(N)    result = true;    % Return false if N is even  if (mod(N, 2) == 0)    result = false;    return;  end    primeFactors = factor(N);  factorsCount = length(primeFactors);    % Check N has at least 2 prime factors and that  % they're distinct  if factorsCount == 1 ...      || factorsCount != length(unique(primeFactors))           result = false;      else       % Check all factors divide N - 1      for factor = primeFactors        if (mod(N - 1, factor - 1) != 0)           result = false;           break;        end     end  end
end
