function result = isLuckyAlternative(N)    result = true;    % Return false if N is even or prime  if (mod(N, 2) == 0 || isprime(N))    result = false;    return;  end    primeFactors = factor(N);  factorsCount = length(primeFactors);    % Check has distinct prime factors  if factorsCount != length(unique(primeFactors))     result = false;      else       % Check all factors divide N - 1      for factor = primeFactors        if (mod(N - 1, factor - 1) != 0)           result = false;           break;        end     end  end
end
