function x = isluckydavis(y)    factors = factor(y);    x=0;    if ~isprime(y)          if mod(y,2)==1            if length(factors)==length(unique(factors))                for i = [1:length(factors)]                    if mod(y-1,factors(i)-1)~=0                         x=0;                        break                    else                        x=1;                    end                end            end        end    end  end