function foxTarget = computeFoxTarget(rabbitPos, foxPos, warehouseNW, warehouseSW)  Y = 2; # index of y coordinate  X = 1; # index of x coordinate    if (isLineOfSight(foxPos, rabbitPos, warehouseNW, warehouseSW))    foxTarget = rabbitPos;    return;  end  % Distance between two points P1 and P2  distance = @(p1, p2)  sqrt((p1(Y) - p2(Y)) ^ 2 + (p1(X) - p2(X))^2);    % bug here consider fox being above warehouse when it loses line of sight  if (foxPos(Y) < warehouseSW(Y))        foxTarget = warehouseSW;  elseif (distance(rabbitPos, warehouseNW) < distance(rabbitPos, warehouseSW))    foxTarget = warehouseNW;  else    foxTarget = warehouseSW;  endif end