function dzdt = modelODE(t, z, burrowPos, rabbitSpeed, foxSpeed, warehouseNW, warehouseSW)      angleBetween = @(p1, p2) atan2(p2(2) - p1(2), (p2(1) - p1(1)));    % z = [Rx, Ry, Fx, Fy]  dzdt = zeros(size(z));  rabbitTheta = angleBetween(z(1:2), burrowPos);    dzdt(1) = rabbitSpeed * cos(rabbitTheta);  dzdt(2) = rabbitSpeed * sin(rabbitTheta);  % fox aims for the rabbit  foxTheta = angleBetween(z(1:2), z(3,4)));  dzdt(3) = cos(foxTheta) * foxSpeed;  dzdt(4) = sin(foxTheta) * foxSpeed;
endfunction
