function dzdt = modelODE(t, z, burrowPos, rabbitSpeed, ...                         foxSpeed, warehouseNW, warehouseSW)  Y = 2; % index of y in pos  X = 1; % index of x in pos    % z = [Rx, Ry, Fx, Fy]    dzdt = zeros(size(z));    % Converted into row vectors to match input parameters  rabbitPos = z(1:2)';  foxPos = z(3:4)';    % rabbit velocity  dzdt(1:2) = computeVelocity(rabbitSpeed, rabbitPos, burrowPos)';   % fox velocity  foxTarget = computeFoxTarget(rabbitPos, foxPos, warehouseNW, warehouseSW);    dzdt(3:4) = computeVelocity(foxSpeed, foxPos, foxTarget)';  
end