function dzdt = modelODE(t, z, burrowPos, rabbitSpeed, foxSpeed, warehouseNW, warehouseSW)      % z = [Rx, Ry, Fx, Fy]  dzdt = zeros(size(z));  rabbitTheta = atan2(burrowPos(2) - z(2), (burrowPos(1) - z(1)));    dzdt(1) = rabbitSpeed * cos(rabbitTheta);  dzdt(2) = rabbitSpeed * sin(rabbitTheta);  % fox aims for the rabbit  foxRatio = (z(2)-z(4)) / (z(1)-z(3));  dzdt(3) = foxSpeed / sqrt(1 + foxRatio ^ 2);  dzdt(4) = foxSpeed * foxRatio / sqrt(1 + foxRatio ^ 2);  foxTheta = atan2((z(2)-z(4)), (z(1)-z(3)));  dzdt(3) = cos(foxTheta) * foxSpeed;  dzdt(4) = sin(foxTheta) * foxSpeed;
endfunction
