function plotScene(burrowPos, warehouseNW, warehouseSW, z, fig, title)    figure(fig);     patch([warehouseSW(1) 900 900 warehouseSW(1)], [warehouseSW(2) warehouseSW(2) warehouseNW(2) warehouseNW(2)], 'FaceColor', [0.6 0.6 0.6], 'LineStyle', 'none');  hold on;   plot(z(:,1), z(:,2))  hold on;  plot(z(:,3), z(:,4), 'LineWidth', 1.8);  hold on;  plot(burrowPos(1), burrowPos(2), 'k*');  hold on;  axis([-14 1000 -600 610]);  hold on;  legend({'Warehouse', 'Rabbit', 'Fox', 'Burrow'});    print("-depsc", title);  hold off;  
end
