function plotScene(burrowPos, warehouseNW, warehouseSW, z, fig)    figure(fig);     patch([warehouseSW(1) 900 900 warehouseSW(1)], [warehouseSW(2) warehouseSW(2) warehouseNW(2) warehouseNW(2)], 'g', 'LineStyle', 'none');  hold on;   plot(z(:,1), z(:,2))  hold on;  plot(z(:,3), z(:,4), 'LineWidth', 1.8);  hold on;  plot(burrowPos(1), burrowPos(2), 'k*');  hold on;  ##  for row = z'##    line = isLineOfSight([row(3) row(4)], [row(1) row(2)], warehouseNW, warehouseSW);##    ##    if line ##      plot([row(1) row(3)], [row(2) row(4)], 'y');##    else##      plot([row(1) row(3)], [row(2) row(4)], 'k');##    endif##    ##    hold on;##  endfor    legend({'Warehouse', 'Rabbit', 'Fox'}, 'Location', 'NorthWest');  hold off;  
endfunction
