function dzdt = realisticModelODE(t, z, burrowPos, ...                                rabbitSpeed, foxSpeed, ...                                warehouseNW, warehouseSW, ...                                rabbitSpeedRate, foxSpeedRate)    % z = [Rx, Ry, Fx, Fy, Dr, Df]    dzdt = zeros(size(z));    % Converted into row vectors to match input parameters  rabbitPos = z(1:2)';  foxPos = z(3:4)';   % rabbit velocity    currentRabbitSpeed = rabbitSpeed * exp(-rabbitSpeedRate * z(5));  dzdt(1:2) = computeVelocity(currentRabbitSpeed, rabbitPos, burrowPos)';   % fox velocity  foxTarget = computeFoxTarget(rabbitPos, foxPos, warehouseNW, warehouseSW);    currentFoxSpeed = foxSpeed * exp(-foxSpeedRate * z(6));    dzdt(3:4) = computeVelocity(currentFoxSpeed, foxPos, foxTarget)';     % The current speeds to compute the distance travelled  dzdt(5) = currentRabbitSpeed;  dzdt(6) = currentFoxSpeed;  end