function [value,isterminal,direction] = stopEvent(t, z, burrowPos)    % z = [Rx, Ry, Fx, Fy]  # Fox catches the rabbit  value(1) = sqrt((z(4) - z(2)) ^ 2 + (z(1) - z(3)) ^ 2) - 0.1;  isterminal(1) = 1;  direction(1) = -1;    # Rabbit reaches it's burrow  value(2) = sqrt((z(2) - burrowPos(2)) ^ 2 + (z(1) - burrowPos(1)) ^ 2) - 0.1;  isterminal(2) = 1;  direction(2) = -1;
end