function figs = readSalesFigures()  fid = fopen('salesfig.csv', 'r'); % open the file  data = textscan ( fid , '%s,%f,%f,%f,%f,%f', -1,'Delimiter',',');  fclose (fid);  figs = struct();    figs.Dates = [];  figs.Days = []; % The number of days of each date since the first one    dates = data(1){1};  for i = 1:numel(dates)    figs.Dates{i} = dates{i};    figs.Days(i) = i;  end    figs.MaxTemp = cell2mat(data(2));  figs.MinTemp = cell2mat(data(3));  figs.Bread =   cell2mat(data(4));  figs.Salad =   cell2mat(data(5));  figs.Lettuce = cell2mat(data(6));  
end
