data = readSalesFigures();x = data.Days(4:end-1);y = data.Bread(4:end-1);####range =x(1 mod([1:numel(data.Bread)]' - 4, 7);plot(x, y, '*');hold on;slopes = [];intercepts = [];for i = [1:7:numel(x)]    xPeriod = x(i:i+6)';  yPeriod = y(i:i+6);    zeroEntries = find(yPeriod <= 0);  xPeriod(zeroEntries) = [];  yPeriod(zeroEntries) = [];    [slope, intercept] = slopeAndIntercept(xPeriod, yPeriod);  slopes = [slopes slope];  intercepts = [intercepts intercept];    plot(x(i:i+6), x(i:i+6) * slope + intercept);  hold on;end##avgSlope = mean(slopes)##avgIntercept = mean(intercepts)##for i = [1:7:numel(x)-7]##  ##  plot(x(i:i+6), x(i:i+6)*avgSlope);##  hold on;##endxlim([1080, 1096]);ylim([800 1000]);####hold on;##plot(x, x .* slope + intercept);##hold on;####xlim([0, 10]);##ylim([300 1200]);######for i = [0:30]####  ####  v = i*7 + 4;####  line([v v], [0 1200]);####  hold on;####end##plot(range, data.Bread, '*', ...##range, data.Salad, 'd', ...##range, data.Lettuce, 's');